import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Todo from './todo.js'
import Header from './header.js'

class App extends Component {
    state = {
      name:'Test List',
      todo:[{
        id:0,
        title:'',
        description:'',
        status: '',
        deadline: ''
      }]
};
addTodo = () =>{
  const todos = this.state.todo;
  todos.push({id: todos.length});
  this.setState({
    todo:todos
  })
}
render(){
  return (
    <div className="App">
        <Header name={this.state.name} addTodo={this.addTodo}/>
        
        <Todo item={this.state.todo} />
 
    </div>
  );
}
}

export default App;
