import React, {Component} from 'react';
import ToDoItem from './toDoItem.js'

class Todo extends Component {
// const Todo = ({item, onChange}) => {
constructor(props) {
    super(props);
    this.state = {
      status: false,
      deadline: ''
    }
}
render(){
  return (<ul>
  {
// console.log(this.props.item.onUpdate)
     this.props.item.map((item,id) => (
       <ToDoItem description={item.description} title={item.title} onChange={(e,id)=> {this.props.onChange(e, item.id)}} onUpdate={(e,id)=> {this.props.onUpdate(e, item.id)}} id={id} status={item.status}/>
   ))
  }
  </ul>)

  }
};



export default Todo;
