import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Todo from './todo.js'
import Header from './header.js'

class App extends Component {
    state = {
      name:'Test List',
      todo:[{
        id:0,
        title:'',
        description:'',
        status: '',
        deadline: ''
      }]
};
addTodo = () =>{
  const todos = this.state.todo;
  todos.push({id: todos.length});
  this.setState({
    todo:todos
  })
}
onChange = (e) => {
   this.setState({name: e.target.value})
}

updateTitle = (e, id) => {
  const todo = {...this.state.todo};
    //this.setState({todo.title: e.target.value})
   console.log(e.target);
   console.log(todo[id.title]);
   console.log(id)
}
//selectall
//trash can at the bottom
render(){
  return (
    <div className="App">
        <Header name={this.state.name} addTodo={this.addTodo} onChange={this.onChange}/>
        <Todo item={this.state.todo} onChange={this.updateTitle} />
 
    </div>
  );
}
}

export default App;
