import React from 'react';

const Header = ({name, addTodo, onChange, deleteTodo, selectAll, allSelected}) => {
  return (
    <header>
    <input name="checkall" type="checkbox" onClick={(id) => {selectAll()}} defaultChecked={allSelected} />
    <input type='text' value={name} onChange={(e)=> {onChange(e)}}/>
    <button onClick={() => {addTodo()}}>+</button>
    <button onClick={() => {deleteTodo()}}> Trash</button>
    </header>
  )
}

export default Header;
