import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Todo from './todo.js'
import Header from './header.js'

var idCounter = 0;

class App extends Component {
    state = {
      name:'Test List',
      todo:[{
        id:0,
        title:'',
        description:'',
        status: false,
        deadline: ''
      }]
};
addTodo = () =>{
  const todos = this.state.todo;
  todos.push({id: idCounter+1, status:false});
  this.setState({
    todo:todos
  })
idCounter++;
console.log(this.state);
}
deleteTodo = () =>{
  this.setState(state => {
      const todo = state.todo.filter((item) => item.status !== true);
      return {
        todo,
      };
})
console.log(this.state);
}

onChange = (e) => {
   this.setState({name: e.target.value})
}

updateTitle = (e, id) => {
  // const todo = {...this.state.todo};
  let value = e.target.value;
  console.log(value);
  console.log(id);
  this.setState(state => {
      const list = state.todo.map((item, j) => {
        if (j === id) {
          return item.title = value;
        } else {
          return item;
        }
      });
      return {
        list,
      };
})
}

updateDescription = (e, id) => {
  // const todo = {...this.state.todo};
  let value = e.target.value;
  console.log(value);
  console.log(id);
  this.setState(state => {
      const list2 = state.todo.map((item, j) => {
        if (j === id) {
          return item.description = value;
        } else {
          return item;
        }
      });
      return {
        list2,
      };
})
}

handleClick = (e, id) => {
  console.log(id);
this.setState(state => {
    const list3 = state.todo.map((item, j) => {
      if (j === id) {
        return item.status = !item.status;
      } else {
        return item;
      }
    });
    return {
      list3,
    };
})
}
//select all
//trash can at the bottom
render(){
  return (
    <div className="App">
        <Header name={this.state.name} addTodo={this.addTodo} onChange={this.onChange} deleteTodo={this.deleteTodo}/>
        <Todo item={this.state.todo} onChange={this.updateTitle} onUpdate={this.updateDescription} onClick={this.handleClick} />
 
    </div>
  );
}
}

export default App;
