import React from 'react';

const TodoItem = ({id, title, description, onChange, onUpdate, status, onClick}) => {
return (<div className='card'>
<li key={id}>
<input value={title} onChange={(e,id)=> {onChange(e,id)}} placeholder='Title'/>
<input value={description} onChange={(e,id)=> {onUpdate(e,id)}} placeholder='Description'/>
<input name="status" type="checkbox" onClick={(e) => {onClick(e)}} onChange={(e) => {onClick(e)}} checked={status} />
{id}{console.log(status)}
</li>
</div>)
}
// <input type="checkbox"/>
// <button> Trash </button>
export default TodoItem;
