import React, {Component} from 'react';
import ToDoItem from './toDoItem.js'

class Todo extends Component {
// const Todo = ({item, onChange}) => {
constructor(props) {
    super(props);
    this.state = {
      status: false
    }
}
render(){
  return (<ul>
  {
// console.log(this.props.item)
     this.props.item.map((item,id) => (
       <ToDoItem description={item.description} title={item.title} onChange={(e)=> {item.onChange(e)}} id={id}/>
   ))
  }
  </ul>)

  }
};



export default Todo;
