import React from 'react';

const TodoItem = ({id, title, description, onChange, onUpdate}) => {
return (<div className='card'>
<li key={id}>
<input value={title} onChange={(e,id)=> {onChange(e,id)}} placeholder='Title'/>
<input value={description} onChange={(e,id)=> {onUpdate(e,id)}} placeholder='Description'/>

{id}
</li>
</div>)
}
// <input type="checkbox"/>
// <button> Trash </button>
export default TodoItem;
